
public class WhileAndDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		while(i<=5){
			System.out.println(i);
			i++;
		}
		
		System.out.println("======\n");
		
		do{
			System.out.println( i);
			i++;
		}while (i<=10);

			
		
		
		
		
	}

}
