
public class OndalikliVeriTipleri {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float sayi1 = 30.5F;
		float sayi2 = 30.5f;
		float sayi3 = 30;
		System.out.print(sayi1 + "\n" + sayi2 + "\n" + sayi3
				+ "\n===================\n");
		
		double dsayi1 = 10;
		double dsayi2 = 13.14;
		System.out.println(dsayi1 + "\n" + dsayi2);
		// degiskenler sonradan guncellenebilir.
		dsayi1 = 20.5;
		System.out.print(dsayi1);
		
		
	}

}
