
public class SayisalVeriTipleri {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte abc = 120 ;
		System.out.println(abc);
		
		short shortDegiskeni = 200;
		System.out.println(abc + shortDegiskeni);
		
		int intDeger = 300213;
		System.out.println(intDeger + shortDegiskeni + abc);
		
		long longDeger = 300000000;
		System.out.println(longDeger);
		
		double doubleDeger = 3.5;
		System.out.print(doubleDeger);
		
	}

}
